contracts/Cliente.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Empresa.sol";

/**
 * @title Cliente
 * @dev Contrato para gestionar clientes de las empresas
 */
contract Cliente is Ownable {
    constructor(address _empresaContract) Ownable(msg.sender) {
        empresaContract = Empresa(_empresaContract);
    }
    struct ClienteData {
        address empresaAddress;
        address clienteAddress;
        uint256 comprasTotales; // Total de compras en tokens EURO
        uint256 fechaRegistro;
        bool activo;
    }

    // Mapeo de empresa => cliente => datos del cliente
    mapping(address => mapping(address => ClienteData)) public clientes;

    // Mapeo de empresa => array de direcciones de clientes
    mapping(address => address[]) public clientesPorEmpresa;

    // Referencia al contrato de empresas
    Empresa public empresaContract;

    // Eventos
    event ClienteRegistrado(address indexed empresaAddress, address indexed clienteAddress);
    event CompraRealizada(address indexed empresaAddress, address indexed clienteAddress, uint256 monto, uint256 nuevoTotal);
    event ClienteDesactivado(address indexed empresaAddress, address indexed clienteAddress);


    modifier soloEmpresaActiva(address _empresaAddress) {
        require(empresaContract.esEmpresaActiva(_empresaAddress), "La empresa no existe o no esta activa");
        _;
    }

    /**
     * @dev Registra un nuevo cliente para una empresa
     */
    function registrarCliente(address _empresaAddress, address _clienteAddress) external onlyOwner soloEmpresaActiva(_empresaAddress) {
        require(_clienteAddress != address(0), "Direccion de cliente no puede ser cero");
        require(!clientes[_empresaAddress][_clienteAddress].activo, "El cliente ya esta registrado para esta empresa");

        clientes[_empresaAddress][_clienteAddress] = ClienteData({
            empresaAddress: _empresaAddress,
            clienteAddress: _clienteAddress,
            comprasTotales: 0,
            fechaRegistro: block.timestamp,
            activo: true
        });

        clientesPorEmpresa[_empresaAddress].push(_clienteAddress);

        emit ClienteRegistrado(_empresaAddress, _clienteAddress);
    }

    /**
     * @dev Registra una compra y actualiza el total del cliente
     */
    function registrarCompra(
        address _empresaAddress,
        address _clienteAddress,
        uint256 _monto
    ) external onlyOwner soloEmpresaActiva(_empresaAddress) {
        require(_monto > 0, "El monto debe ser mayor a cero");

        // Si el cliente no existe, lo registramos automáticamente
        if (!clientes[_empresaAddress][_clienteAddress].activo) {
            clientes[_empresaAddress][_clienteAddress] = ClienteData({
                empresaAddress: _empresaAddress,
                clienteAddress: _clienteAddress,
                comprasTotales: 0,
                fechaRegistro: block.timestamp,
                activo: true
            });

            clientesPorEmpresa[_empresaAddress].push(_clienteAddress);
            emit ClienteRegistrado(_empresaAddress, _clienteAddress);
        }

        clientes[_empresaAddress][_clienteAddress].comprasTotales += _monto;

        emit CompraRealizada(_empresaAddress, _clienteAddress, _monto, clientes[_empresaAddress][_clienteAddress].comprasTotales);
    }

    /**
     * @dev Desactiva un cliente
     */
    function desactivarCliente(address _empresaAddress, address _clienteAddress) external onlyOwner {
        require(clientes[_empresaAddress][_clienteAddress].activo, "El cliente no existe o no esta activo");

        clientes[_empresaAddress][_clienteAddress].activo = false;

        emit ClienteDesactivado(_empresaAddress, _clienteAddress);
    }

    /**
     * @dev Obtiene los datos de un cliente
     */
    function obtenerCliente(address _empresaAddress, address _clienteAddress) external view returns (ClienteData memory) {
        require(clientes[_empresaAddress][_clienteAddress].activo, "El cliente no existe o no esta activo");
        return clientes[_empresaAddress][_clienteAddress];
    }

    /**
     * @dev Obtiene todos los clientes de una empresa
     */
    function obtenerClientesPorEmpresa(address _empresaAddress) external view returns (ClienteData[] memory) {
        address[] memory direcciones = clientesPorEmpresa[_empresaAddress];
        ClienteData[] memory clientesActivos = new ClienteData[](direcciones.length);

        uint256 contadorActivos = 0;
        for (uint256 i = 0; i < direcciones.length; i++) {
            if (clientes[_empresaAddress][direcciones[i]].activo) {
                clientesActivos[contadorActivos] = clientes[_empresaAddress][direcciones[i]];
                contadorActivos++;
            }
        }

        // Crear array del tamaño correcto
        ClienteData[] memory resultado = new ClienteData[](contadorActivos);
        for (uint256 i = 0; i < contadorActivos; i++) {
            resultado[i] = clientesActivos[i];
        }

        return resultado;
    }

    /**
     * @dev Obtiene el número total de clientes de una empresa
     */
    function obtenerTotalClientesPorEmpresa(address _empresaAddress) external view returns (uint256) {
        return clientesPorEmpresa[_empresaAddress].length;
    }

    /**
     * @dev Verifica si un cliente está registrado y activo
     */
    function esClienteActivo(address _empresaAddress, address _clienteAddress) external view returns (bool) {
        return clientes[_empresaAddress][_clienteAddress].activo;
    }

    /**
     * @dev Obtiene el total de compras de un cliente
     */
    function obtenerComprasTotales(address _empresaAddress, address _clienteAddress) external view returns (uint256) {
        if (!clientes[_empresaAddress][_clienteAddress].activo) {
            return 0;
        }
        return clientes[_empresaAddress][_clienteAddress].comprasTotales;
    }
}

contracts/EcommerceSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./EuroToken.sol";
import "./Empresa.sol";
import "./Producto.sol";
import "./Cliente.sol";
import "./Factura.sol";

/**
 * @title EcommerceSystem
 * @dev Contrato principal que integra todo el sistema de e-commerce
 */
contract EcommerceSystem is Ownable, ReentrancyGuard {
    constructor() Ownable(msg.sender) {}
    // Referencias a los contratos
    EuroToken public euroToken;
    Empresa public empresaContract;
    Producto public productoContract;
    Cliente public clienteContract;
    Factura public facturaContract;

    // Estructura para items del carrito
    struct CartItem {
        address empresaAddress;
        uint256 productoId;
        uint256 cantidad;
        uint256 precio;
    }

    // Eventos
    event CompraRealizada(address indexed cliente, address indexed empresa, uint256 numeroFactura, uint256 total);
    event TokensTransferidos(address indexed from, address indexed to, uint256 amount);


    /**
     * @dev Configura las direcciones de los contratos
     */
    function configurarContratos(
        address _euroToken,
        address _empresaContract,
        address _productoContract,
        address _clienteContract,
        address _facturaContract
    ) external onlyOwner {
        euroToken = EuroToken(_euroToken);
        empresaContract = Empresa(_empresaContract);
        productoContract = Producto(_productoContract);
        clienteContract = Cliente(_clienteContract);
        facturaContract = Factura(_facturaContract);
    }

    /**
     * @dev Procesa una compra completa
     */
    function procesarCompra(
        address _empresaAddress,
        CartItem[] memory _items
    ) external nonReentrant {
        require(empresaContract.esEmpresaActiva(_empresaAddress), "La empresa no existe o no esta activa");
        require(_items.length > 0, "El carrito no puede estar vacio");

        uint256 totalCompra = 0;

        // Validar productos y calcular total
        for (uint256 i = 0; i < _items.length; i++) {
            CartItem memory item = _items[i];

            require(item.empresaAddress == _empresaAddress, "Todos los items deben ser de la misma empresa");
            require(productoContract.esProductoActivo(_empresaAddress, item.productoId), "Producto no existe o no esta activo");
            require(item.cantidad > 0, "La cantidad debe ser mayor a cero");

            // Obtener precio del producto
            (,,,uint256 precio,,,) = productoContract.productos(_empresaAddress, item.productoId);
            require(item.precio == precio, "El precio del item no coincide con el precio del producto");

            totalCompra += item.precio * item.cantidad;
        }

        // Verificar que el cliente tenga suficientes tokens
        require(euroToken.balanceOf(msg.sender) >= totalCompra, "Saldo insuficiente de tokens EURO");

        // Transferir tokens del cliente al contrato
        euroToken.transferFrom(msg.sender, address(this), totalCompra);

        // Crear factura
        uint256 numeroFactura = facturaContract.crearFactura(_empresaAddress, msg.sender, totalCompra);

        // Marcar factura como pagada
        facturaContract.marcarFacturaComoPagada(_empresaAddress, numeroFactura);

        // Registrar compra en el cliente
        clienteContract.registrarCompra(_empresaAddress, msg.sender, totalCompra);

        emit CompraRealizada(msg.sender, _empresaAddress, numeroFactura, totalCompra);
    }

    /**
     * @dev Obtiene el balance de tokens EURO de un usuario
     */
    function obtenerBalanceTokens(address _usuario) external view returns (uint256) {
        return euroToken.balanceOf(_usuario);
    }

    /**
     * @dev Obtiene información completa de un producto
     */
    function obtenerInfoProducto(address _empresaAddress, uint256 _productoId) external view returns (
        address empresaAddress,
        uint256 id,
        string memory nombre,
        uint256 precio,
        string memory imagenIPFS,
        bool activo,
        uint256 fechaCreacion
    ) {
        require(productoContract.esProductoActivo(_empresaAddress, _productoId), "Producto no existe o no esta activo");

        return productoContract.productos(_empresaAddress, _productoId);
    }

    /**
     * @dev Obtiene todos los productos de una empresa
     */
    function obtenerProductosEmpresa(address _empresaAddress) external view returns (Producto.ProductoData[] memory) {
        require(empresaContract.esEmpresaActiva(_empresaAddress), "La empresa no existe o no esta activa");
        return productoContract.obtenerProductosPorEmpresa(_empresaAddress);
    }

    /**
     * @dev Obtiene información de una empresa
     */
    function obtenerInfoEmpresa(address _empresaAddress) external view returns (Empresa.EmpresaData memory) {
        return empresaContract.obtenerEmpresa(_empresaAddress);
    }

    /**
     * @dev Obtiene el historial de compras de un cliente
     */
    function obtenerHistorialCompras(address _empresaAddress, address _clienteAddress) external view returns (Factura.FacturaData[] memory) {
        return facturaContract.obtenerFacturasPorCliente(_empresaAddress, _clienteAddress);
    }

    /**
     * @dev Obtiene el total de compras de un cliente
     */
    function obtenerTotalComprasCliente(address _empresaAddress, address _clienteAddress) external view returns (uint256) {
        return clienteContract.obtenerComprasTotales(_empresaAddress, _clienteAddress);
    }

    /**
     * @dev Función de emergencia para retirar tokens del contrato
     */
    function retirarTokens(uint256 _amount) external onlyOwner {
        require(euroToken.balanceOf(address(this)) >= _amount, "Saldo insuficiente en el contrato");
        euroToken.transfer(owner(), _amount);
    }

    /**
     * @dev Función para aprobar tokens (necesaria para las compras)
     */
    function aprobarTokens(uint256 _amount) external {
        euroToken.approve(address(this), _amount);
    }
}

contracts/Empresa.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Empresa
 * @dev Contrato para gestionar empresas en el sistema
 */
contract Empresa is Ownable {
    constructor() Ownable(msg.sender) {}
    struct EmpresaData {
        address empresaAddress;
        string nombre;
        bool activa;
        uint256 fechaCreacion;
    }

    // Mapeo de direcciones a datos de empresa
    mapping(address => EmpresaData) public empresas;

    // Array de direcciones de empresas
    address[] public direccionesEmpresas;

    // Eventos
    event EmpresaRegistrada(address indexed empresaAddress, string nombre);
    event EmpresaActualizada(address indexed empresaAddress, string nuevoNombre);
    event EmpresaDesactivada(address indexed empresaAddress);

    /**
     * @dev Registra una nueva empresa
     */
    function registrarEmpresa(address _empresaAddress, string memory _nombre) external onlyOwner {
        require(_empresaAddress != address(0), "Direccion de empresa no puede ser cero");
        require(bytes(_nombre).length > 0, "El nombre no puede estar vacio");
        require(!empresas[_empresaAddress].activa, "La empresa ya esta registrada");

        empresas[_empresaAddress] = EmpresaData({
            empresaAddress: _empresaAddress,
            nombre: _nombre,
            activa: true,
            fechaCreacion: block.timestamp
        });

        direccionesEmpresas.push(_empresaAddress);

        emit EmpresaRegistrada(_empresaAddress, _nombre);
    }

    /**
     * @dev Actualiza el nombre de una empresa
     */
    function actualizarEmpresa(address _empresaAddress, string memory _nuevoNombre) external onlyOwner {
        require(empresas[_empresaAddress].activa, "La empresa no existe o no esta activa");
        require(bytes(_nuevoNombre).length > 0, "El nombre no puede estar vacio");

        empresas[_empresaAddress].nombre = _nuevoNombre;

        emit EmpresaActualizada(_empresaAddress, _nuevoNombre);
    }

    /**
     * @dev Desactiva una empresa
     */
    function desactivarEmpresa(address _empresaAddress) external onlyOwner {
        require(empresas[_empresaAddress].activa, "La empresa no existe o no esta activa");

        empresas[_empresaAddress].activa = false;

        emit EmpresaDesactivada(_empresaAddress);
    }

    /**
     * @dev Obtiene los datos de una empresa
     */
    function obtenerEmpresa(address _empresaAddress) external view returns (EmpresaData memory) {
        require(empresas[_empresaAddress].activa, "La empresa no existe o no esta activa");
        return empresas[_empresaAddress];
    }

    /**
     * @dev Verifica si una empresa está registrada y activa
     */
    function esEmpresaActiva(address _empresaAddress) external view returns (bool) {
        return empresas[_empresaAddress].activa;
    }

    /**
     * @dev Obtiene el número total de empresas
     */
    function obtenerTotalEmpresas() external view returns (uint256) {
        return direccionesEmpresas.length;
    }

    /**
     * @dev Obtiene todas las direcciones de empresas
     */
    function obtenerTodasLasEmpresas() external view returns (address[] memory) {
        return direccionesEmpresas;
    }
}

contracts/EuroToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title EuroToken
 * @dev Token ERC20 que representa euros en el sistema
 * Este token no es negociable en mercados externos
 */
contract EuroToken is ERC20, Ownable, Pausable {
    // Comisión de entrada (en basis points, 100 = 1%)
    uint256 public entryFee = 250; // 2.5%

    // Comisión de salida (en basis points, 100 = 1%)
    uint256 public exitFee = 250; // 2.5%

    // Dirección del contrato de pagos
    address public paymentContract;

    // Eventos
    event TokensPurchased(address indexed buyer, uint256 euroAmount, uint256 tokenAmount, uint256 fee);
    event TokensWithdrawn(address indexed seller, uint256 tokenAmount, uint256 euroAmount, uint256 fee);
    event FeesUpdated(uint256 newEntryFee, uint256 newExitFee);

    constructor() ERC20("Euro Token", "EURO") Ownable(msg.sender) {
        // No se mintean tokens iniciales
    }

    modifier onlyPaymentContract() {
        require(msg.sender == paymentContract, "Solo el contrato de pagos puede llamar esta funcion");
        _;
    }

    /**
     * @dev Establece la dirección del contrato de pagos
     */
    function setPaymentContract(address _paymentContract) external onlyOwner {
        paymentContract = _paymentContract;
    }

    /**
     * @dev Actualiza las comisiones
     */
    function updateFees(uint256 _entryFee, uint256 _exitFee) external onlyOwner {
        require(_entryFee <= 1000, "Comision de entrada no puede ser mayor al 10%");
        require(_exitFee <= 1000, "Comision de salida no puede ser mayor al 10%");

        entryFee = _entryFee;
        exitFee = _exitFee;

        emit FeesUpdated(_entryFee, _exitFee);
    }

    /**
     * @dev Compra tokens con euros (llamado por el contrato de pagos)
     */
    function purchaseTokens(address buyer, uint256 euroAmount) external onlyPaymentContract whenNotPaused {
        uint256 fee = (euroAmount * entryFee) / 10000;
        uint256 tokenAmount = euroAmount - fee;

        _mint(buyer, tokenAmount);

        emit TokensPurchased(buyer, euroAmount, tokenAmount, fee);
    }

    /**
     * @dev Retira tokens por euros (llamado por el contrato de pagos)
     */
    function withdrawTokens(address seller, uint256 tokenAmount) external onlyPaymentContract whenNotPaused {
        require(balanceOf(seller) >= tokenAmount, "Saldo insuficiente");

        uint256 fee = (tokenAmount * exitFee) / 10000;
        uint256 euroAmount = tokenAmount - fee;

        _burn(seller, tokenAmount);

        emit TokensWithdrawn(seller, tokenAmount, euroAmount, fee);
    }

    /**
     * @dev Pausa el contrato
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Despausa el contrato
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Función para pausar transferencias (requerida por Pausable)
     */
    function _update(
        address from,
        address to,
        uint256 amount
    ) internal override whenNotPaused {
        super._update(from, to, amount);
    }
}

contracts/Factura.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Empresa.sol";

/**
 * @title Factura
 * @dev Contrato para gestionar facturas del sistema
 */
contract Factura is Ownable {
    constructor(address _empresaContract) Ownable(msg.sender) {
        empresaContract = Empresa(_empresaContract);
    }
    struct FacturaData {
        address empresaAddress;
        uint256 numero;
        uint256 fecha;
        address clienteAddress;
        uint256 importeTotal; // Importe en tokens EURO
        bool pagada;
        uint256 fechaCreacion;
    }

    // Mapeo de empresa => número => datos de la factura
    mapping(address => mapping(uint256 => FacturaData)) public facturas;

    // Mapeo de empresa => array de números de factura
    mapping(address => uint256[]) public facturasPorEmpresa;

    // Contador de facturas por empresa
    mapping(address => uint256) public contadorFacturas;

    // Referencia al contrato de empresas
    Empresa public empresaContract;

    // Eventos
    event FacturaCreada(address indexed empresaAddress, uint256 indexed numero, address indexed clienteAddress, uint256 importeTotal);
    event FacturaPagada(address indexed empresaAddress, uint256 indexed numero, uint256 importeTotal);


    modifier soloEmpresaActiva(address _empresaAddress) {
        require(empresaContract.esEmpresaActiva(_empresaAddress), "La empresa no existe o no esta activa");
        _;
    }

    /**
     * @dev Crea una nueva factura
     */
    function crearFactura(
        address _empresaAddress,
        address _clienteAddress,
        uint256 _importeTotal
    ) external onlyOwner soloEmpresaActiva(_empresaAddress) returns (uint256) {
        require(_clienteAddress != address(0), "Direccion de cliente no puede ser cero");
        require(_importeTotal > 0, "El importe debe ser mayor a cero");

        uint256 nuevoNumero = contadorFacturas[_empresaAddress] + 1;
        contadorFacturas[_empresaAddress] = nuevoNumero;

        facturas[_empresaAddress][nuevoNumero] = FacturaData({
            empresaAddress: _empresaAddress,
            numero: nuevoNumero,
            fecha: block.timestamp,
            clienteAddress: _clienteAddress,
            importeTotal: _importeTotal,
            pagada: false,
            fechaCreacion: block.timestamp
        });

        facturasPorEmpresa[_empresaAddress].push(nuevoNumero);

        emit FacturaCreada(_empresaAddress, nuevoNumero, _clienteAddress, _importeTotal);

        return nuevoNumero;
    }

    /**
     * @dev Marca una factura como pagada
     */
    function marcarFacturaComoPagada(address _empresaAddress, uint256 _numero) external onlyOwner {
        require(facturas[_empresaAddress][_numero].empresaAddress != address(0), "La factura no existe");
        require(!facturas[_empresaAddress][_numero].pagada, "La factura ya esta pagada");

        facturas[_empresaAddress][_numero].pagada = true;

        emit FacturaPagada(_empresaAddress, _numero, facturas[_empresaAddress][_numero].importeTotal);
    }

    /**
     * @dev Obtiene los datos de una factura
     */
    function obtenerFactura(address _empresaAddress, uint256 _numero) external view returns (FacturaData memory) {
        require(facturas[_empresaAddress][_numero].empresaAddress != address(0), "La factura no existe");
        return facturas[_empresaAddress][_numero];
    }

    /**
     * @dev Obtiene todas las facturas de una empresa
     */
    function obtenerFacturasPorEmpresa(address _empresaAddress) external view returns (FacturaData[] memory) {
        uint256[] memory numeros = facturasPorEmpresa[_empresaAddress];
        FacturaData[] memory resultado = new FacturaData[](numeros.length);

        for (uint256 i = 0; i < numeros.length; i++) {
            resultado[i] = facturas[_empresaAddress][numeros[i]];
        }

        return resultado;
    }

    /**
     * @dev Obtiene las facturas de un cliente específico
     */
    function obtenerFacturasPorCliente(address _empresaAddress, address _clienteAddress) external view returns (FacturaData[] memory) {
        uint256[] memory numeros = facturasPorEmpresa[_empresaAddress];
        uint256 contador = 0;

        // Contar facturas del cliente
        for (uint256 i = 0; i < numeros.length; i++) {
            if (facturas[_empresaAddress][numeros[i]].clienteAddress == _clienteAddress) {
                contador++;
            }
        }

        // Crear array con el tamaño correcto
        FacturaData[] memory resultado = new FacturaData[](contador);
        uint256 indice = 0;

        for (uint256 i = 0; i < numeros.length; i++) {
            if (facturas[_empresaAddress][numeros[i]].clienteAddress == _clienteAddress) {
                resultado[indice] = facturas[_empresaAddress][numeros[i]];
                indice++;
            }
        }

        return resultado;
    }

    /**
     * @dev Obtiene el número total de facturas de una empresa
     */
    function obtenerTotalFacturasPorEmpresa(address _empresaAddress) external view returns (uint256) {
        return contadorFacturas[_empresaAddress];
    }

    /**
     * @dev Verifica si una factura está pagada
     */
    function esFacturaPagada(address _empresaAddress, uint256 _numero) external view returns (bool) {
        require(facturas[_empresaAddress][_numero].empresaAddress != address(0), "La factura no existe");
        return facturas[_empresaAddress][_numero].pagada;
    }

    /**
     * @dev Obtiene el total de facturas pagadas de una empresa
     */
    function obtenerTotalFacturasPagadas(address _empresaAddress) external view returns (uint256) {
        uint256[] memory numeros = facturasPorEmpresa[_empresaAddress];
        uint256 contador = 0;

        for (uint256 i = 0; i < numeros.length; i++) {
            if (facturas[_empresaAddress][numeros[i]].pagada) {
                contador++;
            }
        }

        return contador;
    }
}

contracts/Producto.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Empresa.sol";

/**
 * @title Producto
 * @dev Contrato para gestionar productos de las empresas
 */
contract Producto is Ownable {
    constructor(address _empresaContract) Ownable(msg.sender) {
        empresaContract = Empresa(_empresaContract);
    }
    struct ProductoData {
        address empresaAddress;
        uint256 id;
        string nombre;
        uint256 precio; // Precio en tokens EURO
        string imagenIPFS; // Hash IPFS de la imagen
        bool activo;
        uint256 fechaCreacion;
    }

    // Mapeo de empresa => id => datos del producto
    mapping(address => mapping(uint256 => ProductoData)) public productos;

    // Mapeo de empresa => array de IDs de productos
    mapping(address => uint256[]) public productosPorEmpresa;

    // Contador de productos por empresa
    mapping(address => uint256) public contadorProductos;

    // Referencia al contrato de empresas
    Empresa public empresaContract;

    // Eventos
    event ProductoCreado(address indexed empresaAddress, uint256 indexed id, string nombre, uint256 precio, string imagenIPFS);
    event ProductoActualizado(address indexed empresaAddress, uint256 indexed id, string nombre, uint256 precio, string imagenIPFS);
    event ProductoDesactivado(address indexed empresaAddress, uint256 indexed id);


    modifier soloEmpresaActiva(address _empresaAddress) {
        require(empresaContract.esEmpresaActiva(_empresaAddress), "La empresa no existe o no esta activa");
        _;
    }

    /**
     * @dev Crea un nuevo producto
     */
    function crearProducto(
        address _empresaAddress,
        string memory _nombre,
        uint256 _precio,
        string memory _imagenIPFS
    ) external onlyOwner soloEmpresaActiva(_empresaAddress) {
        require(bytes(_nombre).length > 0, "El nombre no puede estar vacio");
        require(_precio > 0, "El precio debe ser mayor a cero");
        require(bytes(_imagenIPFS).length > 0, "La imagen IPFS es requerida");

        uint256 nuevoId = contadorProductos[_empresaAddress] + 1;
        contadorProductos[_empresaAddress] = nuevoId;

        productos[_empresaAddress][nuevoId] = ProductoData({
            empresaAddress: _empresaAddress,
            id: nuevoId,
            nombre: _nombre,
            precio: _precio,
            imagenIPFS: _imagenIPFS,
            activo: true,
            fechaCreacion: block.timestamp
        });

        productosPorEmpresa[_empresaAddress].push(nuevoId);

        emit ProductoCreado(_empresaAddress, nuevoId, _nombre, _precio, _imagenIPFS);
    }

    /**
     * @dev Actualiza un producto existente
     */
    function actualizarProducto(
        address _empresaAddress,
        uint256 _id,
        string memory _nombre,
        uint256 _precio,
        string memory _imagenIPFS
    ) external onlyOwner soloEmpresaActiva(_empresaAddress) {
        require(productos[_empresaAddress][_id].activo, "El producto no existe o no esta activo");
        require(bytes(_nombre).length > 0, "El nombre no puede estar vacio");
        require(_precio > 0, "El precio debe ser mayor a cero");
        require(bytes(_imagenIPFS).length > 0, "La imagen IPFS es requerida");

        productos[_empresaAddress][_id].nombre = _nombre;
        productos[_empresaAddress][_id].precio = _precio;
        productos[_empresaAddress][_id].imagenIPFS = _imagenIPFS;

        emit ProductoActualizado(_empresaAddress, _id, _nombre, _precio, _imagenIPFS);
    }

    /**
     * @dev Desactiva un producto
     */
    function desactivarProducto(address _empresaAddress, uint256 _id) external onlyOwner {
        require(productos[_empresaAddress][_id].activo, "El producto no existe o no esta activo");

        productos[_empresaAddress][_id].activo = false;

        emit ProductoDesactivado(_empresaAddress, _id);
    }

    /**
     * @dev Obtiene los datos de un producto
     */
    function obtenerProducto(address _empresaAddress, uint256 _id) external view returns (ProductoData memory) {
        require(productos[_empresaAddress][_id].activo, "El producto no existe o no esta activo");
        return productos[_empresaAddress][_id];
    }

    /**
     * @dev Obtiene todos los productos de una empresa
     */
    function obtenerProductosPorEmpresa(address _empresaAddress) external view returns (ProductoData[] memory) {
        uint256[] memory ids = productosPorEmpresa[_empresaAddress];
        ProductoData[] memory productosActivos = new ProductoData[](ids.length);

        uint256 contadorActivos = 0;
        for (uint256 i = 0; i < ids.length; i++) {
            if (productos[_empresaAddress][ids[i]].activo) {
                productosActivos[contadorActivos] = productos[_empresaAddress][ids[i]];
                contadorActivos++;
            }
        }

        // Crear array del tamaño correcto
        ProductoData[] memory resultado = new ProductoData[](contadorActivos);
        for (uint256 i = 0; i < contadorActivos; i++) {
            resultado[i] = productosActivos[i];
        }

        return resultado;
    }

    /**
     * @dev Obtiene el número total de productos de una empresa
     */
    function obtenerTotalProductosPorEmpresa(address _empresaAddress) external view returns (uint256) {
        return contadorProductos[_empresaAddress];
    }

    /**
     * @dev Verifica si un producto está activo
     */
    function esProductoActivo(address _empresaAddress, uint256 _id) external view returns (bool) {
        return productos[_empresaAddress][_id].activo;
    }
}

